mapper.insert:
		INSERT INTO 
			notice(
	        notice_idx
            , notice_title 
            , notice_content
            , notice_regDate
            , notice_img
            , notice_viewcnt
            , notice_file
            , notice_filename
            , notice_status
	        )
	    VALUES(
	        #{noticeIdx} 
	        , #{noticeTitle}
	        , #{noticeContent}
	        , NOW()
	        , #{noticeImg}
	        , 0
	        , #{noticeFile}
	        , #{noticeFileName}
	        , 1
	        )

mapper.update:
		UPDATE notice SET
			notice_title=#{noticeTitle} 
            , notice_content=#{noticeContent}
            , notice_img=#{noticeImg} 
            , notice_file=#{noticeFile}
            , notice_filename=#{notice_filename}

        WHERE notice_idx=#{noticeIdx}

		UPDATE NOTICE SET
        	notice_viewcnt = notice_viewcnt + 1
        WHERE notice_idx=#{noticeIdx}
