mapper.insert:
		INSERT INTO 
			notice(
	        notice_idx
            , notice_title 
            , notice_content
            , notice_regDate
            , notice_img
            , notice_viewcnt
            , notice_file
            , notice_filename
            , notice_status
	        )
	    VALUES(
	        #{noticeIdx} 
	        , #{noticeTitle}
	        , #{noticeContent}
	        , NOW()
	        , #{noticeImg}
	        , 0
	        , #{noticeFile}
	        , #{noticeFileName}
	        , 1
	        )

mapper.update:
		UPDATE notice SET
			notice_title=#{noticeTitle} 
            , notice_content=#{noticeContent}
            , notice_img=#{noticeImg} 
            , notice_file=#{noticeFile}
            , notice_filename=#{noticeFileName}

        WHERE notice_idx=#{noticeIdx}

mapper.delete:

UPDATE notice SET
	notice_status=1
WHERE notice_idx=#{noticeIdx}

mapper.update:
		UPDATE NOTICE SET
        	notice_viewcnt = notice_viewcnt + 1
        WHERE notice_idx=#{noticeIdx}

mapper.select:
SELECT notice_idx , notice_title , notice_content , date_format(notice_regdate, '%y-%m-%d') as notice_regdate , notice_viewcnt , notice_status , ROWNUM FROM (
SELECT @ROWNUM := @ROWNUM + 1 AS ROWNUM , RN.* FROM (
SELECT notice_idx , notice_title , notice_content , notice_regdate , notice_viewcnt , notice_status FROM notice
WHERE (notice_title LIKE CONCAT('%', #{selectKeyword}, '%') OR notice_content LIKE CONCAT('%', #{selectKeyword}, '%')) AND notice_status = 1
WHERE notice_status=1
) RN, (SELECT @ROWNUM := 0) tmp ) SUB WHERE SUB.ROWNUM BETWEEN #{startRow} and #{endRow}
