mapper.insert:
		INSERT INTO comment(
	        comment_idx
            , post_idx 
            , userinfo_id 
            , parent_idx
            , comment_content
            , comment_regdate
            , comment_status
	        )
	    VALUES(
	        #{commentIdx} 
	        , #{postIdx}
	        , #{userinfoId}



	        , null




	        , #{parentIdx}


	        , #{commentContent}
	        , NOW()
	        , 1
	        )

mapper.update:
		UPDATE comment SET
			comment_content=#{commentContent} 
        WHERE comment_idx=#{commentIdx}

UPDATE comment SET
	comment_status=0
WHERE comment_idx=#{commentIdx}
