mapper.insert:
		INSERT INTO NOTICE(
	        notice_idx
         	, userinfo_id
            , notice_title 
            , notice_content
            , notice_regDate 
            , notice_viewcnt
            , notice_status
	        )
	    VALUES(
	        #{noticeIdx} 
	        , #{userinfoId}
	        , #{noticeTitle}
	        , #{noticeContent}
	        , NOW()
	        , 0
	        , 1
	        )

mapper.update:
		UPDATE notice SET
			notice_title=#{noticeTitle} 
            , notice_content=#{noticeContent} 
        WHERE notice_idx=#{noticeIdx}

		UPDATE NOTICE SET
        	notice_viewcnt = notice_viewcnt + 1
        WHERE notice_idx=#{noticeIdx}
